const data = arguments[0];
const time = arguments[1];

// Calculate Results
const angle = time * 1.0;
const radians = angle * Math.PI / 180.0;
const distance = 0;  // Distance from Center
const offsetX = 0;
const offsetY = 0;
const x = Math.cos(radians) * distance + offsetX;
const y = Math.sin(radians) * distance + offsetY;

// Return Results
return {
    x: x,
    y: y,
};"

